import "core-js/modules/es.array.push.js";
import { reactive, ref, toRaw, onBeforeMount } from "vue";
import { useMenuStore } from "../../store/index";
import { menuListColumns } from "./constColumns";
import { list, addList, editMenu, delMenu } from "@/api/menu";
import { getCurrentInstance } from "@vue/runtime-core";
import { Message } from "@arco-design/web-vue";
import TableContainer from "@/components/tableContainer.vue";
export default {
  __name: 'menuManage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let $bus = getCurrentInstance().appContext.config.globalProperties.$bus;
    const permissions = useMenuStore().getPermission;
    onBeforeMount(() => {
      $bus.emit("updateStructure", [{
        type: "text",
        color: "#333",
        msg: "菜单列表"
      }]);
    });
    // 表格配置信息
    const dataContent = reactive({
      columns: menuListColumns,
      dataSource: [],
      loading: false,
      pagination: {
        pageNum: 1,
        pageSize: 10,
        total: 0
      },
      menuName: "menu"
    });
    const addMenuModal = ref(false);
    const form = reactive({
      type: "",
      name: "",
      parentId: "",
      url: "",
      perms: "",
      sort: undefined,
      icon: ""
    });
    const menuId = ref("");
    const casList = ref([]);
    const treeData = arr => {
      const treeArr = [];
      arr.forEach(item => {
        let obj = {
          label: item.name,
          value: item.id
        };
        if (item.children && item.children.length) {
          obj.children = treeData(item.children);
          treeArr.push(obj);
        } else {
          treeArr.push(obj);
        }
      });
      return treeArr;
    };
    // 获取列表数据
    const getList = () => {
      list().then(res => {
        if (res.code === "000000000000") {
          let arr = [];
          res.data.forEach(item => {
            item.children = [];
            item.key = item.id;
            if (item.parentId === "000000") {
              arr.push(item);
            }
            res.data.forEach(val => {
              if (val.parentId === item.id) {
                item.children.push(val);
              }
            });
          });
          dataContent.dataSource = arr;
          dataContent.pagination.total = Number(dataContent.dataSource.length);
          casList.value = [{
            label: "一级菜单",
            value: "000000",
            children: treeData(arr.slice())
          }];
        }
      });
    };
    getList();
    // 更改当前页码
    const paginationCurrentChange = current => {
      dataContent.pagination.pageNum = current;
      getList();
    };
    // 更改页容量
    const paginationPageSizeChange = pageSize => {
      dataContent.pagination.pageSize = pageSize;
      getList();
    };
    // 添加菜单
    const handleAddMenu = () => {
      addMenuModal.value = true;
      form.type = "", form.name = "", form.parentId = "", form.url = "", form.perms = "", form.sort = undefined, form.icon = "";
    };
    // 编辑
    const handleEditMenu = record => {
      addMenuModal.value = true;
      menuId.value = record.id;
      form.type = record.type, form.name = record.name, form.parentId = record.parentId, form.url = record.url, form.perms = record.perms, form.sort = record.sort, form.icon = record.icon;
    };
    // 删除
    const handleDeleteMenu = record => {
      delMenu(record).then(res => {
        if (res.code === "000000000000") {
          Message.success("删除成功");
          getList();
          $bus.emit("updateMenu");
        } else {
          Message.error(res.message);
        }
      });
    };
    // 添加菜单 确定
    const handleFormOk = () => {
      let values = toRaw(form);
      values.id = menuId.value;
      if (menuId.value === "") {
        addList(values).then(res => {
          if (res.code === "000000000000") {
            Message.success("新建成功!");
            getList();
            $bus.emit("updateMenu");
          } else {
            Message.error(res.message);
          }
        });
      } else {
        editMenu(values).then(res => {
          if (res.code === "000000000000") {
            Message.success("更新成功!");
            getList();
            $bus.emit("updateMenu");
          } else {
            Message.error(res.message);
          }
        });
        menuId.value = "";
      }
    };
    const __returned__ = {
      get $bus() {
        return $bus;
      },
      set $bus(v) {
        $bus = v;
      },
      permissions,
      dataContent,
      addMenuModal,
      form,
      menuId,
      casList,
      treeData,
      getList,
      paginationCurrentChange,
      paginationPageSizeChange,
      handleAddMenu,
      handleEditMenu,
      handleDeleteMenu,
      handleFormOk,
      reactive,
      ref,
      toRaw,
      onBeforeMount,
      get useMenuStore() {
        return useMenuStore;
      },
      get menuListColumns() {
        return menuListColumns;
      },
      get list() {
        return list;
      },
      get addList() {
        return addList;
      },
      get editMenu() {
        return editMenu;
      },
      get delMenu() {
        return delMenu;
      },
      get getCurrentInstance() {
        return getCurrentInstance;
      },
      get Message() {
        return Message;
      },
      TableContainer
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
