        setDataProperty(filterData);
        data = depTreeData.data;
        treeData.data = treeRoot;
      }
      nextTick(() => {
        let nodes = document.getElementsByName('g6Container');
        nodes.forEach(dom => {
          let dept = dom.getAttribute('dept');
          let deptData = data.filter(item => item.label === dept);
          drawGraph(dom, deptData[0]);
        });
      });
    };
    const setNodeData = () => {
      nextTick(() => {
        let nodes = document.getElementsByName('g6Container');
        nodes.forEach(dom => {
          let dept = dom.getAttribute('dept');
          let deptData = data.filter(item => item.label === dept);
          drawGraph(dom, deptData[0]);
